import React,{useState} from 'react'
import Team from "./Team";

const weapons = ["rock", "hand", "scissor"];
const Game = () => {

    state = {
        playerOne: weapons[0],
        playerTwo: weapons[0],
        winner: ""
      };

      const [playerOne, setplayerOne] = useState(weapons[0])
      const [playerTwo, setplayerTwo] = useState(weapons[0])
      const [winner, setWinner] = useState("")
    
     const startGame = () => {
        let counter = 0;
        let gameInterval = setInterval(() => {
          counter++;
             setplayerTwo({
            playerTwo: weapons[Math.floor(Math.random() * weapons.length)],
            winner: ""
          });
          if (counter > 5) {
            clearInterval(gameInterval);
                setplayerTwo({
              winner: selectWinner()
            });
          }
        }, 100);
      };
    
      const selectWinner = () => {
        const { playerOne, playerTwo } = playerTwo;
    
        if (playerOne === playerTwo) {
          return "Oops it's a Tie!";
        } else if (
          (playerOne === "rock" && playerTwo === "scissors") ||
          (playerOne === "scissors" && playerTwo === "paper") ||
          (playerOne === "paper" && playerTwo === "rock")
        ) {
          return "Player One Wins!";
        } else {
          return "Player Two Wins!";
        }
      };
    const  selectWeapon = weapon => {
        setplayerOne({
          playerOne: weapon,
          winner: ""
        });
      };

    return (
        <>
            <h1 style={{ textAlign: "center" }}>Rock Paper Scissors</h1>
        <div>
          <Team weapon={playerOne} />
          <Team weapon={playerTwo} />
        </div>
        <div>
          <button
            className="weaponBtn"
            onClick={() => this.selectWeapon("rock")}
          >
            rock
          </button>
          <button
            className="weaponBtn"
            onClick={() => this.selectWeapon("paper")}
          >
            paper
          </button>
          <button
            className="weaponBtn"
            onClick={() => this.selectWeapon("scissors")}
          >
            scissor
          </button>
        </div>
        <div className="winner">{winner ? this.selectWinner() : null}</div>
        <button type="button" onClick={this.startGame}>
          Start!
        </button>
        </>
    )
}

export default Game
